package capa2;

// TODO: Auto-generated Javadoc

/**
 * The Class Descans.
 */
public class Descans {
	
	/** The NUM. */
	private static int NUM;
	
	/** The id. */
	private int id;  	
	
	/** The tipus. */
	private String tipus;
	
	/** The duracio. */
	private int duracio;
		
	/**
	 * The Constructor.
	 * 
	 * @param duracio the duracio
	 * @param tipus the tipus
	 */
	public Descans(String tipus, int duracio) {
		
		NUM++;
		this.id = NUM;
		this.tipus = tipus;
		this.duracio = duracio;
		
	}
	
	/**
	 * Gets the duracio.
	 * 
	 * @return the int
	 */
	public int GetDuracio() {
		
		return duracio; 
		
	}
	
	/**
	 * Gets the tipus.
	 * 
	 * @return the string
	 */
	public String GetTipus() {
		
		return tipus;
		
	}

	/**
	 * Gets the id.
	 * 
	 * @return the int
	 */
	public int GetId() {
		
		return id;
		
	}

	/**
	 * Sets the id.
	 * 
	 * @param id the id
	 */
	public void SetId(int id) {
		
		this.id = id;
		
	}

	/**
	 * Sets the durada.
	 * 
	 * @param durada the durada
	 */
	public void SetDurada(int durada) {
		
		this.duracio = durada;
		
	}

	/**
	 * Sets the tipus.
	 * 
	 * @param tipus the tipus
	 */
	public void SetTipus(String tipus) {
		
		this.tipus = tipus;
		
	}
	
	/**
	 * To string.
	 * 
	 * @return the string
	 */
	public String ToString()
	{
		return this.tipus + " (" + this.duracio + " min)\n";
	}
	
}
